triple P = (1,1,1), Q = (-1,1,-1), R = (1,1,-1), S = (1,-1,-1), T = (-1,-1,1), U = (-1,1,1);

draw(O--2*I, Arrow3(6));
draw((-2)*J--2*J, Arrow3(6));
draw(O--2*K, Arrow3(6));
draw(O--P);
draw(O--Q);
draw(O--R);
draw(O--S);
draw(O--T);
draw(O--U);
draw(P--Q--R--S--T--U,dashed);

label("$x$", 2.2*I);
label("$y$", 2.2*J);
label("$z$", 2.2*K);

